INSERT INTO `` (`pr_id`,`pr_model_code`,`pr_model_name`,`pr_star`,`pr_market`,`pr_write_id`,`pr_upload_date`,`pr_title`,`pt_text`,`pr_url`) VALUES (1220,'SM-A908','Galaxy A90 5G',5,'11번가','dan****','2020-07-02',NULL,'자급제폰은 처음 사봐요 그래서 걱정이 많았어요 \r\n인터넷주문이기도 하고 사실 걱정 좀 했는데 \r\n \r\n판매자분께서 친절하셨고 \r\n택배도 빨리오고 튼튼하게 보내주셨어요 \r\n믿고받는 우체국택배에 빠른배송! \r\n \r\n폰받고 이틀째 쓰고있는데 정말 좋아용 \r\n폰크기는 생각도 못하고 사서 받고 놀랬지만.. ㅎㅎ \r\ns8쓰다가 바꿨는데 이게 더 폰 속도도 빠르고 좋아용 \r\n화이트주문했다가 KT는 블랙뿐이라서 블랙으로 변경해서 받았는데 색은 별로 중요치 않아서 ㅎㅎㅎ \r\n무튼 좋아요 \r\n굉장히 만족하면서 쓰는 중이고 주변에도 추천중이에요 \r\n번창하세요~ ^^','https://www.11st.co.kr/catalog/catalogDetail.tmall?method=catalogDetail&ctlgNo=19726971&trTypeCd=PW08&trCtgrNo=585021');
