INSERT INTO `` (`pr_id`,`pr_model_code`,`pr_model_name`,`pr_star`,`pr_market`,`pr_write_id`,`pr_upload_date`,`pr_title`,`pt_text`,`pr_url`) VALUES (2232,'SM-G955','GalaxyS8 Plus',4,'11번가','pgg*****','2019-01-15',NULL,'일요일에 입금하고 월요일에 배송시작 화요일에 받았음 \r\n졸 빠름 \r\n제품은 생각보다 아주 괜찮음 \r\n그러나 붙여준다던 액정필름이 구겨져있었는지 무지개색 실금처럼 가로로 보이는거임 \r\n아 이런 교환해야하나 싶었는데 요리보고 저리봐도 필름이 구겨져있는 것처럼 보임 \r\n그래서 필름 뜯어버림 \r\n다행히 핸드폰 하자가 아니었음 \r\n보호필름은 씹덕구렸어서 별 하나뺌 \r\n고속충전기 좋음 \r\n핸드폰케이스가 하나왔는데 안쓰고 버림 \r\n이건 취향차이라.. 그냥 내가 딱딱한 케이스를 안좋아함 \r\n \r\n총평 : 매우 마음에 듬 \r\n \r\n수정한다 : 충전기 대가리 안됨 ㅋ 케이스는 안딱딱한데 빳빳함 그리고 버튼위치에 구멍 뚫려있어서 케이스 씌워보니 버튼이 안눌림 ㅎㅎㅎㅎㅎㅎㅎㅎㅎㅎㅎㅎ 그래서 역시나 버림 아무튼 기계는 좋음','https://www.11st.co.kr/catalog/catalogDetail.tmall?method=catalogDetail&ctlgNo=13666953&trTypeCd=PW08&trCtgrNo=585021');
