INSERT INTO `` (`pr_id`,`pr_model_code`,`pr_model_name`,`pr_star`,`pr_market`,`pr_write_id`,`pr_upload_date`,`pr_title`,`pt_text`,`pr_url`) VALUES (2907,'SM-N950','GalaxyNote8',4,'11번가','ydk****','2018-10-20',NULL,'S급이라고해서 믿고 구매했는데 \r\n직접 픽업하고 차에서 포장지 뜯고 확인해보니 \r\nA급이네요. 왼쪽 상단 모서리에 찍힘(잔기스 아님)이 있는데 S급으로 판매하는건 좀 아닌거 같습니다. \r\n시간이 없어서 교환을 요청하지 않았지만, \r\n다른분에게 판매하실때는 좀 더 확실히 판매하셨으면 좋겠네요.','https://www.11st.co.kr/catalog/catalogDetail.tmall?method=catalogDetail&ctlgNo=13246072&trTypeCd=PW08&trCtgrNo=585021');
