INSERT INTO `` (`pr_id`,`pr_model_code`,`pr_model_name`,`pr_star`,`pr_market`,`pr_write_id`,`pr_upload_date`,`pr_title`,`pt_text`,`pr_url`) VALUES (3114,'SHV-E160','GalaxyNote',5,'11번가','fre******','2018-09-16',NULL,'목욜밤에 구매하고 금욜 점심때쯤 신청서 작성 완료했어요~ \r\n첨에는 대리점에 해당색상 재고가 없는상태라 월요일에 발송된다고 전화받고 화욜에나 오겠네ㅠㅠ 하고있었는데! 5시쯤 재고 들어와서 발송처리 해주셨다길래 오오..!하고 기다렸더니 토요일 오전 열시에 도착했어요ㅋㅋㅋ \r\n강원도 지방이라 오래걸릴줄 알았는데 우체국택배 매직! \r\n첨에 유심 다운로드가 제대로 안돼서 잉??하다가 어쩌다보니 통화 문자 문제없길래 잘 쓰고있어요. 유심재사용이라 필요가 없는건가 싶기도.. \r\n무튼 합리적인 가격에 총알배송으로 만족스럽네여!!','https://www.11st.co.kr/catalog/catalogDetail.tmall?method=catalogDetail&ctlgNo=13666893&trTypeCd=PW08&trCtgrNo=585021');
