INSERT INTO `` (`pr_id`,`pr_model_code`,`pr_model_name`,`pr_star`,`pr_market`,`pr_write_id`,`pr_upload_date`,`pr_title`,`pt_text`,`pr_url`) VALUES (4859,'SM-G988','Galaxy S20 Ultra',5,'11번가','sey*******','2020-02-29',NULL,'22일에 주문해서 더 오래걸릴 줄 알았는데 송장뜨자마자 얼마안있어서 바로 도착했어요. \r\n가족 한 명이 이미 S20플러스 개통해서 쓰고있는거 봐서 그런지 크기는 생각보다 엄청 안크다라고 느껴졌어요. 기존에 S8쓰던거랑 나란히 두고보면 크긴크지만요ㅋㅋ. 기기변경으로 유심구입해서 개통하는데까지 1시간걸렸지만 기다릴만했어요. 기존 패키지에 들어있는 투명케이스 외에도 11번가에서 투명케이스랑 액정필름 동봉해서 보내줘서 좋았어요 :)','https://www.11st.co.kr/catalog/catalogDetail.tmall?method=catalogDetail&ctlgNo=21448542&trTypeCd=PW08&trCtgrNo=585021');
